import styled from 'styled-components'
// 引入（即使是引入图片）都要用 import
import logoPic from '../../statics/logo.png'

export const HeaderWrapper = styled.div`
  display: flex;
  align-items: center;
  height: 106px;
`
export const Logo = styled.a.attrs({
  // 属性的写法：往 attrs 方法中传入对象
  href: '/'
})`
  // 因为 logo 是背景不是图片，背景撑不开盒子，所以必须写宽高
  width: 72px;
  height: 25px;
  margin-left: 91px;
  // 保证图片完整显示在规定范围内
  // background-size: contain
  // 不可以直接写 url 路径，路径相关使用 import
  background: url(${logoPic});
`
export const Nav = styled.ul`
  position: absolute;
  right: 0;
  top: 40px;
  width: 1014px;
  height: 23px;
  margin-right: 76px;
  list-style-type: none;
  // background: pink;
`
export const NavItem = styled.li`
  float: right;
  margin-left: 58px;
  line-height: 23px;
  font-size: 16px;
  font-family: PingFang SC, Helvetica, Arial, sans-serif;
  font-weight: 500;
  color: #555;
  // background: orange;
`
