const defaultState = {
  show: false
}

export default (state = defaultState, action) => {
  switch (action.type) {
    case 'show_download':
      const newState = JSON.parse(JSON.stringify(state))
      newState.show = false
      console.log(newState)
      return newState
    case 'close_download':
      const newState2 = JSON.parse(JSON.stringify(state))
      newState2.show = false
      return newState2
  }
  return state
}
