import styled from 'styled-components'
import smallPic from '../../statics/small.png'

export const Container = styled.div`
  padding-top: 30px;
  border-top: 1px solid #eee;
  box-sizing: border-box;
  // margin: 106px 0 150px;
  // &.gray {
  //   height: 100%;
  //   width: 100%;
  //   background-color: rgba(0, 0, 0, 0.4);
  // }
  // .gray {
  //   position: absolute;
  //   height: 100%;
  //   width: 100%;
  //   background-color: rgba(0, 0, 0, 0.4);
  // }
`

export const NoteWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: start-end;
`

export const NoteColumn = styled.ul`
  margin-right: 20px;
  list-style: none;
  :last-child {
    margin-right: 0;
  }
`

export const Note = styled.div`
  width: 220px;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-radius: 8px;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.1);

  overflow: hidden; //可以让图片也变圆角
  cursor: pointer;
  // :hover
  .note-info {
    position: relative;
  }
  .note-img {
    width: 220px;
  }
  .video {
    position: absolute;
    top: 10px;
    right: 8px;
    font-size: 25px;
    color: #fff;
  }
  h3 {
    margin: 10px 15px;
    line-height: 24px;
    font-size: 16px;
    font-weight: 400;
    color: #000;
  }
  .note-append {
    display: flex;
    justify-content: space-between;
    margin: 0 15px;
  }
  .user,
  .like {
    display: flex;
    align-items: center;
  }
  .user-img {
    width: 28px;
    height: 28px;
    border-radius: 50%;
  }
  span {
    font-size: 12px;
    color: #555;
  }
  .name {
    margin-left: 10px;
  }
  .heart {
    // width: 18px;
    // height: 18px;
    font-size: 18px;
    color: #aaa;
  }
  .likes {
    margin-left: 8px;
  }
`

export const More = styled.div`
  .more {
    text-align: center;
    height: 40px;
    line-height: 40px;
    color: #ff2741;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
  }
`

export const DownloadWapper = styled.div`
  display: block;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1; // 为了底下字不透过来
  width: 280px;
  height: 271px;
  background-color: #fff;
  border-radius: 8px;
  &.hidden {
    display: none;
  }
  .potato {
    position: absolute;
    left: 50%;
    top: 0;
    transform: translate(-50%, -100%);
    height: 100px;
    width: 105px;
  }
  .qr-code {
    display: block;
    margin: 10px auto;
    width: 128px;
    height: 128px;
  }
  p {
    margin-bottom: 18px;
    text-align: center;
    font-size: 14px;
    color: #555;
  }
  .close {
    padding: 10px 0;
    margin-top: 32px;
    height: 20px;
    line-height: 20px;
    text-align: center;
    font-size: 14px;
    font-weight: 600;
    color: #5b92e1;
    border-top: 1px solid #eee;
    cursor: pointer;
  }
`

export const SmallQr = styled.div`
  position: fixed;
  right: 10px;
  bottom: 80px;
  width: 30px;
  height: 30px;
  background: url(${smallPic});
  background-size: contain;
`

export const BigQr = styled.div`
  position: fixed;
  right: 50px;
  bottom: 10px;
  width: 100px;
  height: 120px;
  background: #fff;
  box-shadow: 0 2px 4px #eee;
  border-radius: 5px;
  img {
    width: 86px;
    height: 86px;
    margin: 7px 7px 5px;
  }
  p {
    font-size: 13px;
    color: #555;
    text-align: center;
  }
`
