const defaultState = {
  show: false
}

export default (state = defaultState, action) => {
  switch (action.type) {
    case 'show_download':
      const newState = JSON.parse(JSON.parse(state))
      newState.show = true
      return newState
    case 'close_download':
      return { show: false }
  }
  return state
}
