import styled from 'styled-components'

export const NoteWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: start-end;
`

export const NoteColumn = styled.ul`
  margin-right: 20px;
  list-style: none;
  :last-child {
    margin-right: 0;
  }
`

export const Note = styled.div`
  width: 220px;
  margin-bottom: 20px;
  padding-bottom: 10px;
  border-radius: 8px;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.1);

  overflow: hidden; //可以让图片也变圆角
  cursor: pointer;
  // :hover
  .note-info {
    position: relative;
  }
  .note-img {
    width: 220px;
  }
  .video {
    position: absolute;
    top: 10px;
    right: 8px;
    width: 25px;
    height: 25px;
    color: #fff;
  }
  h3 {
    margin: 10px 15px;
    font-size: 16px;
    font-weight: 400;
    color: #000;
  }
  .note-append {
    display: flex;
    justify-content: space-between;
    margin: 0 15px;
  }
  .user,
  .like {
    display: flex;
    align-items: center;
  }
  .user-img {
    width: 28px;
    height: 28px;
    border-radius: 50%;
  }
  span {
    font-size: 12px;
    color: #555;
  }
  .name {
    margin-left: 10px;
  }
  .heart {
    width: 18px;
    height: 18px;
    color: #ccc;
  }
  .likes {
    margin-left: 8px;
  }
`
export const QrCode = styled.div``
