import styled from 'styled-components'
// 引入（即使是引入图片）都要用 import
import logoPic from '../../statics/logo.png'

export const HeaderWrapper = styled.div`
  // display: flex;
  // align-items: center;

  // 弹性布局兼容多种浏览器的写法（6种）(styled-components 会自动添加浏览器前缀)
  // display: -webkit-box;
  // display: -moz-box;
  // display: -webkit-flex;
  // display: -ms-flexbox;
  // display: box;
  display: flex;

  // 主轴：水平从左到右
  flex-direction: row;

  // 项目在主轴两端对齐
  justify-content: space-between;

  // 项目在交叉轴的中点对齐
  align-items: center;

  position: relative;
  z-index: 3; // 在最上面（不被下载 app 的遮罩遮住）

  width: 100%;
  height: 106px;

  background-color: #fff;
  // &.light-theme {
  //   background-color: #fff;
  // }
  // &.dark-theme {
  //   background-color: #fbfbfb;
  // }
`

export const Logo = styled.div`
  // 为了让 logo 脱标
  // logo 图片外需要有个盒子占位子再设置脱标，不然右边的导航栏会往左跑
  position: relative;

  min-width: 72px;
  // 高度不能省 否则垂直居中会出问题
  height: 25px;
  margin-left: 91px;
  margin-top: 1px;

  // 去掉 a 默认的下划线（改写成 div 了）
  // text-decoration: none;
`

export const LogoPic = styled.img.attrs({
  src: logoPic
})`
  // 让 logo 图片脱标，不被右边文字遮挡
  position: absolute;

  width: 72px;
  height: 25px;
`

export const Nav = styled.ul`
  // 使用 flex 布局
  display: flex;

  // 主轴为水平方向，起点在左端
  flex-direction: row;

  // 项目在主轴上的对齐方式：右对齐
  justify-content: flex-end;

  // 项目在交叉轴的中点对齐
  align-items: center;

  // 设置最小宽度后，盒子不会被浏览器窗口挤压移动变形
  min-width: 1014px;
  height: 23px;
  margin-right: 76px;
  // 如果屏幕小于一个值就是 50px

  // 不要列表小圆点
  list-style-type: none;

  // background: pink;
`

export const NavItem = styled.li`
  position: relative;

  margin-left: 58px;
  line-height: 23px;

  font-size: 16px;
  font-family: PingFang SC, Helvetica, Arial, sans-serif;
  font-weight: 500;
  color: #555;
  cursor: pointer;

  // background: orange;

  // 这个是被选中的标签下面的小横线
  ::before {
    position: absolute;
    bottom: -7px;

    content: '';

    width: 100%;
    height: 2px;

    background-color: #ff2442;
    border-radius: 2px;

    transform: translateX(-100%);

    opacity: 0;

    filter: alpha(opacity=0);
  }

  // 加上 & 适用于样式组件本身带有这个类名，去掉 & 适用于该组件内部子组件或子标签（不含该组件）
  &.current-tab {
    // light-theme
    color: #ff2442;

    ::before {
      transform: translateX(0);

      opacity: 1;

      filter: none;
    }
  }
`
