import styled from 'styled-components'
import police from '../../statics/police.png'

export const FooterWrapper = styled.div`
  width: 1440px;
  height: 110px;
  padding: 50px 0 30px;

  background-color: #fbfbfb;
`

export const Top = styled.ul`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  position: relative; //?

  margin-bottom: 20px;
  min-width: 800px;

  list-style-type: none;
  color: #333;

  &.light-theme {
    color: #333;
  }
  &.dark-theme {
    color: #fff;
  }
`

export const TopItem = styled.div`
  display: inline; //?

  width: 115px;
  margin-left: 200px;

  font-size: 14px;
  font-weight: 500;
  text-align: center;
  cursor: pointer;

  :hover {
    color: #fe2543;
  }
  :first-child {
    margin-left: 0;
  }
`

export const Bottom = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`

export const BottomItem = styled.div`
  line-height: 18px;
  font-size: 12px;
  font-weight: 400;
  color: #999;
`

export const BottomA = styled.a`
  text-decoration: none;
  color: #999;
`
export const Police = styled.div`
  width: 18px;
  height: 18px;
  background: url(${police});
`

