import React from 'react'
import { connect } from 'react-redux'
import { AuthorWrapper } from '../style'

// 详情页
const Author = props => {
  return (
    <AuthorWrapper>
      <h3 className='title'>笔记作者</h3>
      <div className='author-info'>
        <img
          alt=''
          className='author-img'
          src='https://img.xiaohongshu.com/avatar/5b30a969d1d3b974147e91d8.jpg@80w_80h_90q_1e_1c_1x.jpg'
        />
        <i className='img-border' />
        <span className='author-name'>我是宋奶茶</span>
      </div>
    </AuthorWrapper>
  )
}

const mapState = state => ({})

const mapDispatch = dispatch => ({})

export default connect(
  mapState,
  mapDispatch
)(Author)
