import { fromJS } from 'immutable'
import * as actionTypes from './actionTypes'

const defaultState = fromJS({
  bigPic: [],
  currentId: 1,
  nextId: 1
})

export default (state = defaultState, action) => {
  switch (action.type) {
    case actionTypes.CHANGE_STORE_DISCOVERY:
      return state.set('bigPic', fromJS(action.data).get('bigPic'))
    case actionTypes.NEXT_SWITCH:
      // return state.merge({
      //   nextId: state.get('currentId') + 1,
      //   currentId: state.get('currentId') + 1
      // })
      return state.set('nextId', state.get('currentId') + 1)
    case actionTypes.NEXT_SWITCH:
      return state.set('currentId', state.get('nextId'))
    default:
      return state
  }
}
