import { fromJS } from 'immutable'
import * as actionTypes from './actionTypes'

const defaultState = fromJS({
  show: false,
  showBig: false,
  note: [
    {
      noteImg:
        'https://ci.xiaohongshu.com/d8f3bcee-72b2-5674-ba3c-c32f2d3f7090?imageView2/2/w/1080/format/jpg',
      title: '日本明治奶粉（奶块包装）',
      userImg:
        'https://img.xiaohongshu.com/avatar/5c1b9793e7421400013ad215.jpg@80w_80h_90q_1e_1c_1x.jpg',
      userName: '东京坂本龍妈',
      likes: 52
    },
    {
      noteImg:
        'https://ci.xiaohongshu.com/d8f3bcee-72b2-5674-ba3c-c32f2d3f7090?imageView2/2/w/1080/format/jpg',
      title: '日本明治奶粉（奶块包装）',
      userImg:
        'https://img.xiaohongshu.com/avatar/5c1b9793e7421400013ad215.jpg@80w_80h_90q_1e_1c_1x.jpg',
      userName: '东京坂本龍妈',
      likes: 52
    },
    {
      noteImg:
        'https://ci.xiaohongshu.com/d8f3bcee-72b2-5674-ba3c-c32f2d3f7090?imageView2/2/w/1080/format/jpg',
      title: '日本明治奶粉（奶块包装）',
      userImg:
        'https://img.xiaohongshu.com/avatar/5c1b9793e7421400013ad215.jpg@80w_80h_90q_1e_1c_1x.jpg',
      userName: '东京坂本龍妈',
      likes: 52
    }
  ]
})

export default (state = defaultState, action) => {
  switch (action.type) {
    case actionTypes.SHOW_DOWNLOAD:
      return state.set('show', true)
    case actionTypes.CLOSE_DOWNLOAD:
      return state.set('show', false)
    case actionTypes.SHOW_BIG:
      return state.set('showBig', true)
    case actionTypes.HIDDEN_BIG:
      return state.set('showBig', false)
    default:
      return state
  }
}
