import React from 'react'
import { connect } from 'react-redux'
import { CommentWrapper, Reply } from '../style'

// 详情页
const Comment = props => {
  return (
    <CommentWrapper>
      <h3>
        <i />
        <span>笔记评论</span>
      </h3>
      <div>
        <img />
        <span>芒果超好吃鸭</span>
        <span className='comment-date'>09-10</span>
        <i className='agree' />
        <span className='replies'>2 回复</span>
        <Reply />
      </div>
    </CommentWrapper>
  )
}

const mapState = state => ({})

const mapDispatch = dispatch => ({})

export default connect(
  mapState,
  mapDispatch
)(Comment)
