import { fromJS } from 'immutable'
import * as actionTypes from './actionTypes'

const defaultState = fromJS({
  bigPic: [],
  currentId: 0,
  nextId: 0
})

export default (state = defaultState, action) => {
  switch (action.type) {
    case actionTypes.CHANGE_STORE_DISCOVERY:
      console.log('aa')
      return state.merge({
        // 对于要转成 immutable 对象的 JS 对象，可以先用点的写法写到最后再转
        bigPic: fromJS(action.data.bigPic),
        // 把第一张图的 id 同时放进 currentId 和 nextId 中
        currentId: fromJS(action.data.bigPic[0].id),
        nextId: fromJS(action.data.bigPic[0].id)
      })
    case actionTypes.NEXT_PIC:
      // if
      return state.set('nextId', state.get('bigPic') + 1)
    case actionTypes.CHANGE_CURRENT_ID:
      return state.set('currentId', state.get('nextId'))
    default:
      return state
  }
}
