import React from 'react'
import { connect } from 'react-redux'
import { DownloadWrapper, Download } from './style'
// import { actionCreators } from './store'

const DownloadApp = props => (
  <DownloadWrapper className={props.show ? '' : 'hidden'}>
    <Download className={props.show ? '' : 'hidden'}>
      <img
        alt=''
        src='//ci.xiaohongshu.com/0feac0b2-2134-438c-8b59-3ddeec95b811@r_640w_640h.jpg'
        className='potato'
      />
      <img
        alt=''
        src='//ci.xiaohongshu.com/8aa9236a-43ef-44f0-97d3-3167847de82a@r_640w_640h.jpg'
        className='qr-code'
      />
      <p>扫码下载小红书app</p>
      <p>与全世界的小红薯一起标记生活</p>
      <div className='close' onClick={props.handleClose}>
        关闭
      </div>
    </Download>
  </DownloadWrapper>
)

const mapState = state => ({
  show: state.getIn(['downloadApp', 'show'])
})

// const mapDispatch = dispatch => ({
//   handleClose () {
//     dispatch(actionCreators.closeDownload())
//   }
// })

export default connect(
  mapState,
  null
)(DownloadApp)
