import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { fromJS } from 'immutable'
import {
  CommentWrapper,
  CommentTitle,
  CommentContent,
  Comment,
  User,
  UserInfo,
  CommentStats,
  Content,
  Replies,
  Reply
} from '../style'
import DownloadApp from '../../../common/downloadApp'
import { actionCreators } from '../../../common/downloadApp/store'

// 详情页
class Comments extends PureComponent {
  render () {
    return (
      <CommentWrapper>
        <CommentTitle>
          <i className='title' />
          <span className='title'>笔记评论</span>
        </CommentTitle>

        <CommentContent>
          {this.props.commentList.map(item => (
            <Comment key={item.get('id')}>
              {/* 评论人、回复数 */}
              <div className='comment-info'>
                <User>
                  <img
                    alt=''
                    className='user-avatar'
                    src={item.get('userAvatar')}
                  />

                  <UserInfo>
                    <h4 className='user-nickname'>{item.get('userName')}</h4>
                    <span className='comment-date'>
                      {item.get('commentDate')}
                    </span>
                  </UserInfo>
                </User>

                <CommentStats>
                  <i className='agree' />
                  <span
                    className='agree-sum'
                    style={{
                      display: item.get('agreeSum') !== 0 ? 'block' : 'none'
                    }}
                  >
                    {item.get('agreeSum')}
                  </span>
                  <span className='reply-action'>回复</span>
                </CommentStats>
              </div>

              {/* 评论内容 */}
              <Content>{item.get('commentContent')}</Content>

              {/* 评论的回复 */}
              <Replies
                style={{
                  display:
                    item.get('replyList') !== fromJS([]) ? 'block' : 'none'
                }}
              >
                {item.get('replyList').map(item => (
                  <Reply key={item.get('id')}>
                    <span className='replier'>
                      {item.get('replier') + ' : '}
                    </span>
                    <p className='reply-content'>{item.get('replyContent')}</p>
                  </Reply>
                ))}
              </Replies>
            </Comment>
          ))}

          <div className='more' onClick={this.props.handleMoreClick}>
            查看更多评论
          </div>
        </CommentContent>

        <DownloadApp />
      </CommentWrapper>
    )
  }
}

const mapState = state => ({
  commentList: state.getIn(['discovery', 'commentList'])
})

const mapDispatch = dispatch => ({
  handleMoreClick () {
    dispatch(actionCreators.showDownload())
  }
})

export default connect(
  mapState,
  mapDispatch
)(Comments)
