import React, { Fragment } from 'react'
import { connect } from 'react-redux'
import { smallQr, bigQr } from '../style'

const QrCode = props => (
  <Fragment>
    <smallQr />
    <bigQr />
  </Fragment>
)

const mapState = state => ({
  show: state.explore.show
})

const mapDispatch = dispatch => ({
  handleMore () {
    const action = {
      type: 'show_download'
    }
    dispatch(action)
  },
  handleClose () {
    const action = {
      type: 'close_download'
    }
    dispatch(action)
  }
})

export default connect(
  mapState,
  mapDispatch
)(QrCode)
