import * as actionTypes from './actionTypes'

const defaultState = {
  show: false
}

export default (state = defaultState, action) => {
  const newState = JSON.parse(JSON.stringify(state))
  switch (action.type) {
    case actionTypes.SHOW_DOWNLOAD:
      newState.show = true
      return newState
    case actionTypes.CLOSE_DOWNLOAD:
      newState.show = false
      return newState
    default:
      return state
  }
}
