import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { CSSTransition } from 'react-transition-group'
import { Carousel, Switch, SmallPics, SmallPic } from '../style'
import { actionCreators } from '../store'

// 详情页
class NoteImg extends PureComponent {
  // constructor (props) {
  //   super(props)
  //   this.state = {
  //     b: false
  //   }
  // }

  render () {
    return (
      <div>
        <Carousel>
          {/* Carousel 包含大图和切换按钮 */}
          <ul className='big-pic'>
            {this.props.bigPic.map(item => (
              <CSSTransition
                key={item.get('id')}
                in={item.get('currentId') === this.props.currentId}
                classNames='left'
                timeout={1000}
              >
                <li
                  style={{
                    background:
                      'url(' + item.get('url') + ') no-repeat center/contain'
                  }}
                />
              </CSSTransition>
            ))}
          </ul>

          <Switch className='left' onClick={this.handleLeft.bind(this)} />
          <Switch className='right' onClick={this.handleRight.bind(this)} />
        </Carousel>

        <SmallPics>
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic className='active' />
          <SmallPic />
        </SmallPics>
      </div>
    )
  }

  componentDidMount () {
    this.props.changeDiscovery()
  }
}

const mapState = state => ({
  bigPic: state.getIn(['discovery', 'bigPic']),
  currentId: state.getIn(['discovery', 'currentId']),
  nextId: state.getIn(['discovery', 'nextId'])
})

const mapDispatch = dispatch => ({
  // handleLeft () {
  //   dispatch()
  // },
  handleRight () {
    dispatch(actionCreators.nextSwitch())
  },
  changeDiscovery () {
    dispatch(actionCreators.getDiscovery())
  }
})

export default connect(
  mapState,
  mapDispatch
)(NoteImg)

