import axios from 'axios'
import * as actionTypes from './actionTypes'
import { actionCreators } from '.'

export const getDiscovery = () => {
  return dispatch => {
    axios
      .get('/api/discovery.json')
      .then(res => {
        dispatch(changeStoreDiscovery(res.data.data))
      })
      .catch(() => {
        alert('获取数据失败，请检查 json 文件是否存在')
      })
  }
}

export const nextAddOne = () => ({
  type: actionTypes.NEXT_ADD_ONE
})

export const changeCurrentId = () => ({
  type: actionTypes.CHANGE_CURRENT_ID
})

// 供内部使用的方法（不导出）
const changeStoreDiscovery = data => ({
  type: actionTypes.CHANGE_STORE_DISCOVERY,
  data
})
