import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { CSSTransition } from 'react-transition-group'
import { Carousel, Switch, SmallPics, SmallPic } from '../style'
import { actionCreators } from '../store'

// 详情页
class NoteImg extends PureComponent {
  // constructor (props) {
  //   super(props)
  //   this.state = {
  //     b: false
  //   }
  // }

  render () {
    return (
      <div>
        <Carousel>
          {/* Carousel 包含大图和切换按钮 */}
          {/* // <CSSTransition in={this.props.} classNames='left' timeout={1000}> */}
          <ul className='big-pic'>
            {this.props.bigPic.map(item => (
              <div key={item.get('id')}>
                <li
                  style={{
                    background:
                      'url(' + item.get('url') + ') no-repeat center/contain'
                  }}
                />
              </div>
            ))}
          </ul>

          {/* // </CSSTransition> */}
          <Switch className='left' onClick={this.handleLeft.bind(this)} />
          <Switch className='right' onClick={this.handleRight.bind(this)} />
        </Carousel>

        <SmallPics>
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic className='active' />
          <SmallPic />
        </SmallPics>
      </div>
    )
  }

  handleLeft () {
    this.setState(() => ({
      b: true
    }))
  }
  handleRight () {
    this.setState(() => ({
      b: false
    }))
  }

  componentDidMount () {
    this.props.changeDiscovery()
  }
}

const mapState = state => ({
  bigPic: state.getIn(['discovery', 'bigPic'])
})

const mapDispatch = dispatch => ({
  // handleLeft () {
  //   thi
  //   console.log(a)
  // },
  // handleRight () {
  //   console.log(a)
  //   console.log('right')
  // },
  changeDiscovery () {
    dispatch(actionCreators.getDiscovery())
  }
})

export default connect(
  mapState,
  mapDispatch
)(NoteImg)

