import React, { Fragment } from 'react'
import { connect } from 'react-redux'
import { SmallQr, BigQr } from '../style'
import { actionCreators } from '../store'

const QrCode = props => (
  <Fragment>
    <SmallQr onMouseOver={props.handleShowBig} />
    <BigQr className={props.showBig ? '' : 'hidden'}>
      <img src='//ci.xiaohongshu.com/8aa9236a-43ef-44f0-97d3-3167847de82a@r_640w_640h.jpg' />
      <p>扫我下载App</p>
    </BigQr>
  </Fragment>
)

const mapState = state => ({
  showBig: state.getIn(['explore', 'showBig'])
})

const mapDispatch = dispatch => ({
  handleShowBig () {
    dispatch(actionCreators.showBig())
  }
})

export default connect(
  mapState,
  mapDispatch
)(QrCode)
