import React from 'react'
import { connect } from 'react-redux'
import { BarWrapper, AuthorInfo } from '../style'

// 详情页
const ShareBar = props => {
  return (
    <BarWrapper>
      <AuthorInfo>
        <img />
        <span>我是宋奶茶</span>
      </AuthorInfo>
      <ShareButton>
        <span />
        <span>一起来分享给朋友们看看吧：</span>
      </ShareButton>
    </BarWrapper>
  )
}

const mapState = state => ({})

const mapDispatch = dispatch => ({})

export default connect(
  mapState,
  mapDispatch
)(ShareBar)
