import { fromJS } from 'immutable'
import * as actionTypes from './actionTypes'

const defaultState = fromJS({
  bigPic: [],
  currentId: 1,
  nextId: 1
})

export default (state = defaultState, action) => {
  switch (action.type) {
    case actionTypes.CHANGE_STORE_DISCOVERY:
      console.log('aa')
      return state.merge({
        bigPic: fromJS(action.data).get('bigPic')
        // ,
        // currentId: fromJS(action.data).getIn(['bigPic', 'id']),
        // nextId: fromJS(action.data).getIn(['bigPic', 'id'])
      })
    case actionTypes.NEXT_ADD_ONE:
      // if
      return state.set('nextId', state.get('currentId') + 1)
    case actionTypes.CHANGE_CURRENT_ID:
      return state.set('currentId', state.get('nextId'))
    default:
      return state
  }
}
