import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { Container, More } from './style'
import { actionCreators } from './store'
import Notes from './components/Notes'
import DownloadApp from '../../common/downloadApp'

class Explore extends PureComponent {
  render () {
    return (
      <Container>
        <Notes />
        <More onClick={this.handleMore.bind(this)}>查看更多</More>
        <DownloadApp onRef={this.onRef.bind(this)} />
      </Container>
    )
  }

  // onRef 在子组件的 componentDidMount 中调用，把子组件实例（连同子组件方法一起）传给父组件
  onRef (ref) {
    this.downloadApp = ref
  }
  // 子组件挂载完毕后，父组件方可调用子组件的方法（调用必须写在父组件的异步方法或父组件的 componentDidMount 中）
  handleMore () {
    this.downloadApp.props.handleMore()
  }
}

const mapDispatch = dispatch => ({
  handleMore () {
    dispatch(actionCreators.showDownload())
  },
  handleClose () {
    dispatch(actionCreators.closeDownload())
  }
})

export default connect(
  null,
  mapDispatch
)(Explore)
