import { fromJS } from 'immutable'
import * as actionTypes from './actionTypes'

// 记住，要想页面变化，一定要改变数据
const defaultState = fromJS({
  // NoteImg
  pics: [],
  picSum: 1,
  currentId: 0,
  nextId: 0,
  direction: 'left',
  fixed: true,

  // NoteContent
  title: '',
  content: '',
  like: 0,
  comment: 0,
  star: 0,
  date: '',

  // ShareBar
  authorImg: '',
  authorName: ''
})

export default (state = defaultState, action) => {
  switch (action.type) {
    // NoteImg
    case actionTypes.CHANGE_IMG_DATA:
      // 对于要转成 immutable 对象的 JS 对象，可以先用点的写法写到最后再转
      return state.merge({
        pics: fromJS(action.data.pics),
        picSum: action.data.pics.length
      })
    case actionTypes.CHANGE_CURRENT_ID:
      return state.set('currentId', state.get('nextId'))
    case actionTypes.NEXT_PIC:
      if (state.get('currentId') === state.get('picSum') - 1) {
        return state.merge({ nextId: 0, direction: 'left' })
      }
      return state.merge({
        nextId: state.get('currentId') + 1,
        direction: 'left'
      })
    case actionTypes.LAST_PIC:
      if (state.get('currentId') === 0) {
        return state.merge({
          nextId: state.get('picSum') - 1,
          direction: 'right'
        })
      }
      return state.merge({
        nextId: state.get('currentId') - 1,
        direction: 'right'
      })
    case actionTypes.SMALL_PIC_CLICK:
      return state.set('nextId', action.index)
    case actionTypes.TOGGLE_SHARE_BAR_FIXED:
      return state.set('fixed', action.show)

    // NoteContent
    case actionTypes.CHANGE_CONTENT_DATA:
      return state.merge({
        title: fromJS(action.data.text.title),
        content: fromJS(action.data.text.content),
        like: fromJS(action.data.text.like),
        comment: fromJS(action.data.text.comment),
        star: fromJS(action.data.text.star),
        date: fromJS(action.data.text.date)
      })

    default:
      return state
  }
}
