import styled from 'styled-components'
import leftPic from '../../statics/left.png'
import rightPic from '../../statics/right.png'

// 详情页
export const CardWrapper = styled.div`
  display: flex;
  justify-content: center;
  background: #fbfbfb;
`

export const LeftCard = styled.div`
  width: 500px;
  margin-right: 30px;
  background: pink;
`

export const RightCard = styled.div`
  width: 320px;
  height: 700px;
  background: skyblue;
`
export const NoteInfo = styled.div``
export const Carousel = styled.div`
  position: relative;
  width: 500px;
  height: 500px;
  background: #fbfbfb;
  border-radius: 4px;
  overflow: hidden; // 保证背景色和里面的盒子不超出圆角

  ul {
    list-style: none;
  }
  li {
    display: none;
    // flex 貌似和上面冲突
    // display: flex;
    // justify-content: center;
    // align-items: center;
    width: 500px;
    height: 500px;

    &.current-pic {
      display: block;
    }
  }
  img {
    // 块元素才能用 margin 居中
    display: block;
    margin: 0 auto;
    // 图片等比例缩放，不超出父盒子（最大宽高是父盒子宽高）
    max-width: 100%;
    max-height: 100%;
  }
`
export const SmallPic = styled.div``
export const Switch = styled.div`
  // 相对定位在 Carousel 上
  position: absolute;
  top: 50%;
  width: 30px;
  height: 30px;
  cursor: pointer;

  &.left {
    left: 10px;
    background: url(${leftPic});
    background-size: contain; // 背景图等比缩小
  }
  &.right {
    right: 10px;
    background: url(${rightPic});
    background-size: contain; // 背景图等比缩小
  }

  :hover:after {
    position: absolute;
    top: 0;
    left: 0;
    content: '';
    // span 元素
    width: 100%;
    height: 100px;
    background: rgba(0, 2, 4, 0.2);
  }
`
export const NoteTitle = styled.div``
export const CardContent = styled.div``
export const CardInfo = styled.div``
export const ShareBar = styled.div``
export const Comment = styled.div``
