import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { actionCreators } from '../store'
import {
  BarWrapper,
  AuthorInfo,
  MiddleBorder,
  ShareButton,
  Icons,
  WechatCode
} from '../style'

// 详情页
class ShareBar extends PureComponent {
  render () {
    return (
      <BarWrapper className={this.props.fixed ? 'fixed' : ''}>
        <AuthorInfo>
          <img
            alt=''
            className='author-img'
            src='https://img.xiaohongshu.com/avatar/5b30a969d1d3b974147e91d8.jpg@80w_80h_90q_1e_1c_1x.jpg'
          />
          <i className='img-border' />
          <span className='author-name'>我是宋奶茶</span>
        </AuthorInfo>
        <MiddleBorder />
        <ShareButton>
          <span className='share'>一起来分享给朋友们看看吧：</span>
          <Icons>
            <i className='wechat'>
              <WechatCode className='wechat-code'>
                <p>分享至微信</p>
              </WechatCode>
            </i>
            <i className='weibo' />
            <i className='qq' />
          </Icons>
        </ShareButton>
      </BarWrapper>
    )
  }

  componentDidMount () {
    // 在组件第一次挂载完毕时调用
    this.bindEvents()
  }

  bindEvents () {
    window.addEventListener('scroll', this.props.toggleShareBarFixed)
  }
}

const mapState = state => ({
  fixed: state.getIn(['discovery', 'fixed'])
})

const mapDispatch = dispatch => ({
  toggleShareBarFixed () {
    // console.log(document.documentElement.scrollTop)
    if (document.documentElement.scrollTop < 500) {
      dispatch(actionCreators.toggleShareBarFixed(true))
    } else {
      dispatch(actionCreators.toggleShareBarFixed(false))
    }
  }
})

export default connect(
  mapState,
  mapDispatch
)(ShareBar)
