import styled from 'styled-components'
// 引入（即使是引入图片）都要用 import
import logoPic from '../../statics/logo.png'

export const HeaderWrapper = styled.div`
  // display: flex;
  // align-items: center;

  // 弹性布局兼容多种浏览器的写法（6种）
  display: -webkit-box;
  display: -moz-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: box;
  display: flex;

  // 主轴：水平从左到右
  -webkit-box-orient: horizontal;
  -moz-box-orient: horizontal;
  -o-box-orient: horizontal;
  -webkit-flex-direction: row;
  -ms-flex-direction: row;
  flex-direction: row;

  // 项目在主轴两端对齐
  -webkit-box-pack: justify;
  -moz-box-pack: justify;
  -o-box-pack: justify;
  -ms-flex-pack: justify;
  -webkit-justify-content: space-between;
  justify-content: space-between;

  // 项目在交叉轴的中点对齐
  -webkit-box-align: center;
  -moz-box-align: center;
  -o-box-align: center;
  -ms-flex-align: center;
  -webkit-align-items: center;
  align-items: center;

  // position: relative;

  width: 100%;
  height: 106px;

  font-size: 16px;
  font-family: PingFang SC, Helvetica, Arial, sans-serif;
  font-weight: 500;

  &.light-theme {
    background-color: #fff;
    background-color: #fbfbfb;
  }
  &.dark-theme {
  }
`

export const Logo = styled.a.attrs({
  // 属性的写法：往 attrs 方法中传入对象
  href: '/'
})`
  // // 使用 flex 布局
  // display: -webkit-box;
  // display: -moz-box;
  // display: -webkit-flex;
  // display: -ms-flexbox;
  // display: box;
  // display: flex;

  // // 项目在交叉轴的中点对齐
  // -webkit-box-align: center;
  // -moz-box-align: center;
  // -o-box-align: center;
  // -ms-flex-align: center;
  // -webkit-align-items: center;
  // align-items: center;

  // 为了让 logo 脱标
  // logo 图片外需要有个盒子占位子再设置脱标，不然右边的导航栏会往左跑
  position: relative;
  display: block;

  width: 72px;
  height: 25px;
  margin-left: 91px;

  // 保证图片完整显示在规定范围内
  // background-size: contain

  // 不可以直接写 url 路径，路径相关使用 import
  // background: url(${logoPic});
`

export const LogoPic = styled.img.attrs({
  src: logoPic
})`
  // 让 logo 图片脱标，不被右边文字遮挡
  position: absolute;

  width: 100%;
  height: 100%;

  // 保证图片完整显示在规定范围内
  // background-size: contain
`

export const Nav = styled.ul`
  // 使用 flex 布局
  display: -webkit-box;
  display: -moz-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: box;
  display: flex;

  // 主轴为水平方向，起点在左端
  -webkit-box-orient: horizontal;
  -moz-box-orient: horizontal;
  -o-box-orient: horizontal;
  -webkit-flex-direction: row;
  -ms-flex-direction: row;
  flex-direction: row;

  // 项目在主轴上的对齐方式：右对齐
  -webkit-box-pack: end;
  -moz-box-pack: end;
  -o-box-pack: end;
  -ms-flex-pack: end;
  -webkit-justify-content: flex-end;
  justify-content: flex-end;

  // 项目在交叉轴的中点对齐
  -webkit-box-align: center;
  -moz-box-align: center;
  -o-box-align: center;
  -ms-flex-align: center;
  -webkit-align-items: center;
  align-items: center;

  // 设置最小宽度后，盒子不会被浏览器窗口挤压移动变形
  min-width: 1014px;
  height: 23px;
  margin-right: 50px;

  list-style-type: none;

  // background: pink;
`

export const NavItem = styled.li`
  margin-left: 58px;
  line-height: 23px;

  font-size: 16px;
  font-family: PingFang SC, Helvetica, Arial, sans-serif;
  font-weight: 500;
  color: #555;
  cursor: pointer;

  // background: orange;

  &.current-tab {
  }
`
