const defaultState = {
  show: false
}

export default (state = defaultState, action) => {
  const newState = JSON.parse(JSON.stringify(state))
  switch (action.type) {
    case 'show_download':
      newState.show = true
      return newState
    case 'close_download':
      // const newState = JSON.parse(JSON.stringify(state))
      newState.show = false
      return newState
  }
  return state
}
