import React, { PureComponent, Fragment } from 'react'
import { connect } from 'react-redux'
import { actionCreators } from './store'
import Header from '../../common/header'
import Footer from '../../common/footer'
import { BackgroundVideo, Content, IPhone, Slogan } from './style'

class Home extends PureComponent {
  render () {
    return (
      <Fragment>
        {/* Header 的3个参数都需要传，Footer 传 theme 即可 */}
        <Header theme='dark' tab='home' url={this.props.match.url} />
        <BackgroundVideo>
          <video
            src={this.props.videoUrl}
            poster={this.props.poster}
            autoPlay='autoplay'
            muted='muted'
            loop='loop'
          />
        </BackgroundVideo>
        <Content>
          <IPhone>
            <video
              src='https://dc.xhscdn.com/a10d0710-80f1-11e9-9036-5948d2131083/new5.27.mp4'
              poster='//ci.xiaohongshu.com/700a0de6-c9ee-4c1c-8e02-94e948d9f611'
              loop='loop'
              preload='auto'
            />
            <div
              style={{
                background:
                  'url("//ci.xiaohongshu.com/3ca6607e-d4a5-4cb9-b455-a746713d8283") no-repeat center/contain'
              }}
            />
          </IPhone>
          <Slogan />
        </Content>
        <Footer theme='dark' />
      </Fragment>
    )
  }

  componentDidMount () {
    this.props.getHomeData()
  }
}

const mapState = state => ({
  videoUrl: state.getIn(['home', 'videoUrl']),
  poster: state.getIn(['home', 'poster'])
})

const mapDispatch = dispatch => ({
  getHomeData () {
    dispatch(actionCreators.getHomeData())
  }
})
export default connect(
  mapState,
  mapDispatch
)(Home)
