const defaultState = {
  show: false
}

export default (state = defaultState, action) => {
  const newState = JSON.parse(JSON.stringify(state))
  switch (action.type) {
    case 'show_download':
      newState.show = true
      return newState
    case 'close_download':
      newState.show = false
      return newState
    default:
      return state
  }
}
