import React, { Fragment } from 'react'
import { connect } from 'react-redux'
import { Container, More } from './style'
import { actionCreators } from './store'
import Notes from './components/Notes'
import DownloadApp from '../../common/downloadApp'

const Explore = props => {
  return (
    <Fragment>
      <Container>
        <Notes />
        <More className='more' onClick={handleMore}>
          查看更多
        </More>
        <DownloadApp onRef={onRef.bind(this)} />
      </Container>
    </Fragment>
  )
}

const onRef = ref => {
  this.downloadApp = ref
}
const handleMore = () => {
  this.downloadApp.handleMore()
}

const mapDispatch = dispatch => ({
  handleMore () {
    dispatch(actionCreators.showDownload())
  },
  handleClose () {
    dispatch(actionCreators.closeDownload())
  }
})

export default connect(
  null,
  mapDispatch
)(Explore)
