import styled from 'styled-components'

export const BackgroundVideo = styled.div`
  //只占满窗口（用 absolute 会超过窗口）
  position: fixed;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  // Header 和 Footer 都设置了 z-index 所以不会遮挡

  video {
    // 最小宽高是整个父盒子内容区（至少占满窗口）
    min-width: 100%;
    min-height: 100%;
  }
`

export const Content = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-60%, -60%);
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`

export const IPhone = styled.div`
  margin-rigth: 60px;
  video {
    position: absolute;
    width: 300px;
    height: 600px;
    padding: 18px 20px;
    box-sizing: border-box;
  }
  div {
    position: absolute;
    width: 300px;
    height: 600px;
  }
`

export const Slogan = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;

  .logo {
    width: 143px;
    height: 50px;
  }
  h2 {
    margin-top: 13px;
    font-size: 48px;
    font-weight: 500;
    color: #fff;
    white-space: nowrap;
  }
`
