import React, { Fragment } from 'react'
import { connect } from 'react-redux'
import { Container, More, QrCode } from './style'
import Notes from './components/Notes'
import DownloadApp from './components/DownloadApp'

const Explore = props => {
  return (
    <Fragment>
      <Container>
        <Notes />
        <More>
          <div className='more' onClick={props.handleMore}>
            查看更多
          </div>
        </More>
        <DownloadApp />
      </Container>
      <QrCode>1</QrCode>
    </Fragment>
  )
}

const mapState = state => ({
  show: state.explore.show
})

const mapDispatch = dispatch => ({
  handleMore () {
    const action = {
      type: 'show_download'
    }
    dispatch(action)
  },
  handleClose () {
    const action = {
      type: 'close_download'
    }
    dispatch(action)
  }
})

export default connect(
  mapState,
  mapDispatch
)(Explore)
