import styled from 'styled-components'
import leftPic from '../../statics/left.png'
import leftHoverPic from '../../statics/left-hover.png'
import rightPic from '../../statics/right.png'
import rightHoverPic from '../../statics/right-hover.png'
import p1 from '../../statics/p1.webp'
import p2 from '../../statics/p2.webp'
import p3 from '../../statics/p3.webp'
import p4 from '../../statics/p4.webp'
import p5 from '../../statics/p5.webp'
import p6 from '../../statics/p6.webp'
import tagHeart from '../../statics/tag-heart.png'
import tagComment from '../../statics/tag-comment.png'
import tagStar from '../../statics/tag-star.png'
import wechatPic from '../../statics/wechat.png'
import weiboPic from '../../statics/weibo.png'
import qqPic from '../../statics/qq.png'
import wechatHoverPic from '../../statics/wechat-hover.png'
import weiboHoverPic from '../../statics/weibo-hover.png'
import qqHoverPic from '../../statics/qq-hover.png'
import agreePic from '../../statics/heart.png'

// 详情页
export const CardWrapper = styled.div`
  display: flex;
  justify-content: center;
  padding: 24px 0 36px;
  background: #fbfbfb;
`

export const LeftCard = styled.div`
  width: 500px;
  margin-right: 30px;
  // background: pink;
`

export const RightCard = styled.div`
  width: 320px;
  height: 700px;
  background: skyblue;
`
export const Carousel = styled.div`
  position: relative;
  width: 500px;
  height: 500px;
  background: #fbfbfb;
  border-radius: 4px;
  overflow: hidden; // 保证背景色和里面的盒子不超出圆角

  ul {
    list-style: none;
  }
  li {
    display: none;
    // flex 貌似和上面冲突
    // display: flex;
    // justify-content: center;
    // align-items: center;
    width: 500px;
    height: 500px;

    &.current-pic {
      display: block;
    }
  }
  img {
    // 块元素才能用 margin 居中
    display: block;
    margin: 0 auto;
    // 图片等比例缩放，不超出父盒子（最大宽高是父盒子宽高）
    max-width: 100%;
    max-height: 100%;
  }
`

export const Switch = styled.div`
  // 相对定位在 Carousel 上
  position: absolute;
  top: 50%;
  width: 30px;
  height: 30px;
  cursor: pointer;

  &.left {
    left: 10px;
    background: url(${leftPic}) no-repeat;
    background-size: contain; // 背景图等比缩小（和背景图一起写才生效）
    :hover {
      // 悬停时换灰色小图标
      background: url(${leftHoverPic}) no-repeat;
      background-size: contain; // 背景图等比缩小
    }
  }
  &.right {
    right: 10px;
    background: url(${rightPic}) no-repeat;
    background-size: contain; // 背景图等比缩小
    :hover {
      background: url(${rightHoverPic}) no-repeat;
      background-size: contain; // 背景图等比缩小
    }
  }
`

// 缩略图
export const SmallPics = styled.ul`
  display: flex;
  justify-content: center;
  width: 500px;
  margin-top: 10px;
  list-style: none;
`

export const SmallPic = styled.li`
  width: 40px;
  height: 40px;
  margin-right: 5px;
  overflow: hidden;
  border-radius: 4px;
  cursor: pointer;
  opacity: 0.6;
  // 样式变化时的过渡
  transition: opacity 0.8s ease;

  :nth-child(1) {
    background: url(${p1});
    background-size: cover;
    background-position: 50%; // 水平垂直居中
  }
  :nth-child(2) {
    background: url(${p2});
    background-size: cover;
    background-position: 50%;
  }
  :nth-child(3) {
    background: url(${p3});
    background-size: cover;
    background-position: 50%;
  }
  :nth-child(4) {
    background: url(${p4});
    background-size: cover;
    background-position: 50%;
  }
  :nth-child(5) {
    background: url(${p5});
    background-size: cover;
    background-position: 50%;
  }
  :nth-child(6) {
    background: url(${p6});
    background-size: cover;
    background-position: 50%;
    // margin-right: 0; // 官方没去掉最后一个的右边距
  }
  &.active {
    border: 1px solid #ff2741;
    box-sizing: border-box;
    opacity: 1;
    box-shadow: 0 3px 8px 0 rgba(255, 82, 103, 0.5);
  }
`

// 文字部分
export const ContentWrapper = styled.div`
  margin-top: 20px;
`

export const NoteTitle = styled.div`
  font-size: 24px;
  font-weight: 500;
`

export const NoteText = styled.div`
  p {
    margin: 1em 0;
  }
`

export const NoteTags = styled.div`
  margin-top: 30px;
  font-size: 16px;

  i {
    display: inline-block;
    width: 20px;
    height: 20px;
    vertical-align: middle;
  }
  .like i {
    background: url(${tagHeart}) no-repeat;
    background-size: contain;
  }
  .comment i {
    background: url(${tagComment}) no-repeat;
    background-size: contain;
  }
  .star i {
    background: url(${tagStar}) no-repeat;
    background-size: contain;
  }
  .like,
  .comment {
    margin-right: 25px;
  }
  span span {
    margin-left: 3px;
    color: #555;
    vertical-align: middle;
  }
`

export const PublishDate = styled.div`
  margin-top: 30px;
  color: #999;
`

// 底部分享条
export const BarWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-top: 30px;
  width: 500px;
  height: 50px;
  border-radius: 25px;
  border: 1px solid #eee;
  box-sizing: border-box;
  background: #fff;
  // box-shadow: 0 1px 20px 0 rgba(0, 0, 0, 0.15);

  :hover .share {
    font-weight: 500;
    // 盒子阴影
  }
`

export const AuthorInfo = styled.div`
  position: relative;
  margin-left: 10px;
  cursor: pointer;
  .author-img {
    width: 30px;
    height: 30px;
    border-radius: 50%;
  }
  .img-border {
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 30px;
    height: 30px;
    border-radius: 50%;

    content: '';
    border: 1px solid rgba(0, 0, 0, 0.15);
    box-sizing: border-box;
  }
  .author-name {
    position: absolute;
    top: 50%;
    left: 45px;
    transform: translateY(-50%);
    width: 82px;
    font-size: 14px;
    font-weight: 500;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
`

export const Partition = styled.span`
  display: inline-block;
  width: 3px;
  height: 19px;
  margin: 0 25px 0 135px;
  background-color: #d8d8d8;
  border-radius: 1.5px;
`

export const ShareButton = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  i {
    display: inline-block;
    width: 26px;
    height: 26px;
    margin-left: 7px;
  }

  .wechat {
    background: url(${wechatPic}) no-repeat;
    background-size: contain;
    :hover {
      background: url(${wechatHoverPic}) no-repeat;
      background-size: contain;
    }
  }
