import React, { Fragment } from 'react'
import { connect } from 'react-redux'
import {
  Container,
  NoteWrapper,
  NoteColumn,
  Note,
  More,
  DownloadApp,
  QrCode
} from './style'
import Notes from './components/Notes'

const Explore = props => {
  return (
    <Fragment>
      <Container>
        <Notes />
        <More>
          <div className='more' onClick={props.handleMore}>
            查看更多
          </div>
        </More>
        {/* <div className={props.show ? 'gray' : ''}> */}
        <DownloadApp className={props.show ? '' : 'hidden'}>
          <img
            alt=''
            src='//ci.xiaohongshu.com/0feac0b2-2134-438c-8b59-3ddeec95b811@r_640w_640h.jpg'
            className='potato'
          />
          <img
            alt=''
            src='//ci.xiaohongshu.com/8aa9236a-43ef-44f0-97d3-3167847de82a@r_640w_640h.jpg'
            className='qr-code'
          />
          <p>扫码下载小红书app</p>
          <p>与全世界的小红薯一起标记生活</p>
          <div className='close' onClick={props.handleClose}>
            关闭
          </div>
        </DownloadApp>
        {/* </div> */}
      </Container>
      <QrCode>1</QrCode>
    </Fragment>
  )
}

const mapState = state => ({
  show: state.explore.show
})

const mapDispatch = dispatch => ({
  handleMore () {
    const action = {
      type: 'show_download'
    }
    dispatch(action)
  },
  handleClose () {
    const action = {
      type: 'close_download'
    }
    dispatch(action)
  }
})

export default connect(
  mapState,
  mapDispatch
)(Explore)
