import { fromJS } from 'immutable'
import actionTypes from './actionTypes'

const defaultState = fromJS({
  isHomeMounted: false
})

export default (state = defaultState, action) => {
  switch (action.type) {
    case actionType.CHANGE_HOME_MOUNTED:
      return state.set('isHomeMounted', true)
  }
}
