import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { CSSTransition, TransitionGroup } from 'react-transition-group'
import { Carousel, Switch, SmallPics, SmallPic } from '../style'
import { actionCreators } from '../store'

// 详情页
class NoteImg extends PureComponent {
  render () {
    return (
      <div>
        <Carousel>
          {/* Carousel 包含大图和切换按钮 */}
          <ul className='big-pic'>
            <TransitionGroup>
              {this.props.bigPic.map(item => (
                <CSSTransition
                  key={item.get('id')}
                  in={this.props.currentId}
                  classNames='left'
                  timeout={1000}
                >
                  <li
                    className={
                      item.get('id') === this.props.currentId
                        ? 'current-pic'
                        : ''
                    }
                    style={{
                      background:
                        'url(' + item.get('url') + ') no-repeat center/contain',
                      display:
                        item.get('id') === this.props.currentId ||
                        item.get('id') === this.props.nextId
                          ? 'block'
                          : 'none'
                    }}
                  />
                </CSSTransition>
              ))}
            </TransitionGroup>
          </ul>

          <Switch
            className='left'
            // onClick={this.handleLeft.bind(this)}
          />
          <Switch className='right' onClick={this.props.handleRight} />
        </Carousel>

        <SmallPics>
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic />
          <SmallPic className='active' />
          <SmallPic />
        </SmallPics>
      </div>
    )
  }

  componentDidMount () {
    this.props.changeDiscovery()
  }
}

const mapState = state => ({
  bigPic: state.getIn(['discovery', 'bigPic']),
  currentId: state.getIn(['discovery', 'currentId']),
  nextId: state.getIn(['discovery', 'nextId'])
})

const mapDispatch = dispatch => ({
  changeDiscovery () {
    dispatch(actionCreators.getDiscovery())
  },
  // handleLeft () {
  //   dispatch()
  // },
  handleRight () {
    dispatch(actionCreators.nextSwitch())
  }
})

export default connect(
  mapState,
  mapDispatch
)(NoteImg)

