import React, { PureComponent } from 'react'
import { connect } from 'react-redux'
import { actionCreators } from '../store'
import {
  ContentWrapper,
  NoteTitle,
  NoteText,
  NoteTags,
  PublishDate
} from '../style'

// 详情页
class NoteContent extends PureComponent {
  render () {
    return (
      <ContentWrapper>
        <NoteTitle>
          {this.props.title}
          {/* 🦋🦋喂！👵奶奶喊你去优衣库领取织好的毛衣 */}
        </NoteTitle>

        {/* 使标签能够被解析，而不是被当做字符串 */}
        <NoteText dangerouslySetInnerHTML={{ __html: this.props.content }}>
          {/* <p>优衣库新进毛衣选手➰小花🌸毛衣</p>
        <p>初秋穿搭的👋选单品➰毛衣</p>
        <p>小花款有两个色～一个红色 一个蓝灰色</p>
        <p>蓝灰色那款白人𝘨𝘦𝘯𝘨显白🥚</p>
        <p>搭好就很出彩</p>
        <p>怕出错就选红色款啦～</p>
        <p>还有一款是纯色哒 毛衣上有纹路</p>
        <p>真的很像奶奶👵织的毛衣 哈哈哈哈哈哈</p>
        <p>pick哪件呢？🤔</p>
        <p>🌼🌼🌼🌼🌼</p>
        <p>
          初秋穿搭 穿搭 秋季穿搭 穿搭女 秋冬穿搭 小个子穿搭 秋装 早秋穿搭 优衣库
          优衣库试衣间 梨形身材穿搭 cos 优衣库穿搭 学生穿搭 半身裙穿搭 毛衣
        </p> */}
        </NoteText>

        <NoteTags>
          <span className='like'>
            <i />
            <span>101</span>
          </span>
          <span className='comment'>
            <i />
            <span>20</span>
          </span>
          <span className='star'>
            <i />
            <span>60</span>
          </span>

          <PublishDate>{'发布于 ' + this.props.date}</PublishDate>
        </NoteTags>
      </ContentWrapper>
    )
  }

  componentDidMount () {
    this.props.getNoteContent()
  }
}

const mapState = state => ({
  title: state.getIn(['discovery', 'title']),
  content: state.getIn(['discovery', 'content']),
  date: state.getIn(['discovery', 'date'])
})

const mapDispatch = dispatch => ({
  getNoteContent () {
    dispatch(actionCreators.getNoteContent())
  }
})

export default connect(
  mapState,
  mapDispatch
)(NoteContent)
