import React, { PureComponent } from 'react'
import { Container, More } from './style'
import Notes from './components/Notes'
import DownloadApp from '../../common/downloadApp'

class Explore extends PureComponent {
  constructor (props) {
    super(props)
    this.onRef = this.onRef.bind(this)
  }

  render () {
    return (
      <Container>
        <Notes />
        <More
          // 子组件挂载完毕后，父组件方可调用子组件的方法（调用必须写在回调函数或父组件的异步方法或父组件的 componentDidMount 中）
          onClick={() => {
            this.downloadApp.props.handleMore()
          }}
        >
          查看更多
        </More>
        <DownloadApp onRef={this.onRef} />
      </Container>
    )
  }

  // onRef 在子组件的 componentDidMount 中调用，把子组件实例（连同子组件方法一起）传给父组件
  onRef (ref) {
    this.downloadApp = ref
  }
}

export default Explore
