import styled from 'styled-components'
import leftPic from '../../statics/left.png'
import leftHoverPic from '../../statics/left-hover.png'
import rightPic from '../../statics/right.png'
import rightHoverPic from '../../statics/right-hover.png'

// 详情页
export const CardWrapper = styled.div`
  display: flex;
  justify-content: center;
  padding: 24px 0 36px;
  background: #fbfbfb;
`

export const LeftCard = styled.div`
  width: 500px;
  margin-right: 30px;
  background: pink;
`

export const RightCard = styled.div`
  width: 320px;
  height: 700px;
  background: skyblue;
`
export const NoteInfo = styled.div``
export const Carousel = styled.div`
  position: relative;
  width: 500px;
  height: 500px;
  background: #fbfbfb;
  border-radius: 4px;
  overflow: hidden; // 保证背景色和里面的盒子不超出圆角

  ul {
    list-style: none;
  }
  li {
    display: none;
    // flex 貌似和上面冲突
    // display: flex;
    // justify-content: center;
    // align-items: center;
    width: 500px;
    height: 500px;

    &.current-pic {
      display: block;
    }
  }
  img {
    // 块元素才能用 margin 居中
    display: block;
    margin: 0 auto;
    // 图片等比例缩放，不超出父盒子（最大宽高是父盒子宽高）
    max-width: 100%;
    max-height: 100%;
  }
`
export const SmallPics = styled.ul`
  display: flex;
  justify-content: center;
  width: 500px;
  margin-top: 10px;
  list-style: none;
`
export const SmallPic = styled.li`
  width: 40px;
  height: 40px;
  margin-right: 5px;
  :nth-child(1) {
    background: url('../../stastics/p1.webp');
    background-size: cover;
    background-position: 50%;
  }
`
export const Switch = styled.div`
  // 相对定位在 Carousel 上
  position: absolute;
  top: 50%;
  width: 30px;
  height: 30px;
  cursor: pointer;

  &.left {
    left: 10px;
    background: url(${leftPic}) no-repeat;
    background-size: contain; // 背景图等比缩小（和背景图一起写才生效）
    :hover {
      // 悬停时换灰色小图标
      background: url(${leftHoverPic}) no-repeat;
      background-size: contain; // 背景图等比缩小
    }
  }
  &.right {
    right: 10px;
    background: url(${rightPic}) no-repeat;
    background-size: contain; // 背景图等比缩小
    :hover {
      background: url(${rightHoverPic}) no-repeat;
      background-size: contain; // 背景图等比缩小
    }
  }
`
export const NoteTitle = styled.div``
export const CardContent = styled.div``
export const CardInfo = styled.div``
export const ShareBar = styled.div``
export const Comment = styled.div``
